/**
 * Helper to apply text style
 * @param {string} text 
 * @param {string} type 'h1' | 'h2' | 'p' | 'small'
 * @param {string} colorKey 'Primary' | 'Secondary' | etc.
 */
export function styled(text, type = 'p', colorKey = 'TextPrimary') {
    const color = Colors.Semantic[colorKey] || Colors.Brand[colorKey] || colorKey;
    const font = type.startsWith('h') ? Typography.Headings : Typography.Body;

    // Simple HTML wrapper for Rich Text elements
    return `<span style="font-family:${font.Family}; font-weight:${font.Weight}; color:${color};">${text}</span>`;
}
